mapper.select:

select

from data_node

    id = #{id, jdbcType=INTEGER}

        and tenant = #{tenant,jdbcType=VARCHAR}

    and is_deleted = 0


select

from data_node

    name = #{name, jdbcType=VARCHAR}
    and type = #{type, jdbcType=VARCHAR}
    and is_deleted = 0

        and tenant = #{tenant,jdbcType=VARCHAR}


select

from data_node


        and name = #{name, jdbcType=VARCHAR}


        and type = #{type, jdbcType=VARCHAR}


        and (
        name like CONCAT('%', #{keyword}, '%')
        or display_name like CONCAT('%', #{keyword}, '%')
        or type like CONCAT('%', #{keyword}, '%')
        or url like CONCAT('%', #{keyword}, '%')
        )


        and status = #{status, jdbcType=INTEGER}


        and tenant = #{tenant,jdbcType=VARCHAR}

    and is_deleted = 0

order by modify_time desc

select

from data_node


        and type = #{type, jdbcType=VARCHAR}


        and url = #{url, jdbcType=VARCHAR}


        and username = #{username, jdbcType=VARCHAR}


        and token = #{token, jdbcType=VARCHAR}


        and ext_params = #{extParams, jdbcType=LONGVARCHAR}

    and tenant = #{tenant, jdbcType=LONGVARCHAR}
    and is_deleted = 0

mapper.update:
update data_node set name = #{name, jdbcType=VARCHAR}, display_name = #{displayName, jdbcType=VARCHAR}, type = #{type, jdbcType=VARCHAR}, url = #{url, jdbcType=VARCHAR}, username = #{username, jdbcType=VARCHAR}, token = #{token, jdbcType=VARCHAR}, ext_params = #{extParams, jdbcType=LONGVARCHAR}, description = #{description,jdbcType=VARCHAR}, in_charges = #{inCharges, jdbcType=VARCHAR}, status = #{status, jdbcType=INTEGER}, is_deleted = #{isDeleted, jdbcType=INTEGER}, modifier = #{modifier, jdbcType=VARCHAR}, version = #{version, jdbcType=INTEGER} + 1
id = #{id, jdbcType=INTEGER}
and tenant = #{tenant,jdbcType=VARCHAR}
and version = #{version, jdbcType=INTEGER}

mapper.delete:

delete
from data_node

    id = #{id, jdbcType=INTEGER}

        and tenant = #{tenant, jdbcType=VARCHAR}
