mapper.select:
select

from stream_source
where task_map_id = #{taskMapId,jdbcType=INTEGER}
and agent_ip = #{agentIp, jdbcType=VARCHAR}
and is_deleted = 0
limit 1

select count(1)
from stream_source

    is_deleted = 0

        and inlong_group_id = #{groupId, jdbcType=VARCHAR}


        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

select count(1)
from stream_source


        and inlong_group_id = #{groupId, jdbcType=VARCHAR}


        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

    and task_map_id is not NULL

select

from stream_source

    is_deleted = 0

        and inlong_group_id = #{request.inlongGroupId, jdbcType=VARCHAR}


        and inlong_stream_id = #{request.inlongStreamId, jdbcType=VARCHAR}


        and source_type = #{request.sourceType, jdbcType=VARCHAR}


        and data_node_name = #{request.dataNodeName, jdbcType=VARCHAR}


        and inlong_cluster_name = #{request.inlongClusterName, jdbcType=VARCHAR}


        and (
        inlong_group_id like CONCAT('%', #{request.keyword}, '%')
        or inlong_stream_id like CONCAT('%', #{request.keyword}, '%')
        or source_name like CONCAT('%', #{request.keyword}, '%')
        )


        and status = #{request.status, jdbcType=INTEGER}


        and status in

            #{status}


    and task_map_id is NULL



        order by ${request.orderField} ${request.orderType}


        order by create_time desc

select

from stream_source

    is_deleted = 0
    and inlong_group_id = #{groupId, jdbcType=VARCHAR}

        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}


        and source_name = #{sourceName, jdbcType=VARCHAR}

select

from stream_source

    is_deleted = 0

        and status in

            #{item}


    limit #{limit, jdbcType=INTEGER}

select

from stream_source

    is_deleted = 0

        and status in

            #{item}



        and source_type in

            #{item}


    limit #{limit, jdbcType=INTEGER}

select

from stream_source

    is_deleted = 0

        and status in

            #{item}



        and source_type in

            #{item}



        and agent_ip = #{agentIp, jdbcType=VARCHAR}

    and inlong_cluster_name = #{clusterName, jdbcType=VARCHAR}

select

from stream_source

    is_deleted = 0

        and status in

            #{item}



        and source_type in

            #{item}


    and agent_ip is NULL
    and task_map_id is NULL
    and inlong_cluster_name = #{clusterName, jdbcType=VARCHAR}

select
from stream_source
(inlong_cluster_name = #{clusterName, jdbcType=VARCHAR}) and (find_in_set( #{agentIp, jdbcType=VARCHAR}, agent_ip) or agent_ip ='All') and `status` in
#{item}
and uuid = #{uuid, jdbcType=VARCHAR}

select distinct (source_type)
from stream_source

    is_deleted = 0

        and inlong_group_id = #{groupId, jdbcType=VARCHAR}


        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

    for update

select id
from stream_source

    status not in (99, 110)
    and is_deleted = 0

        and inlong_cluster_name = #{clusterName, jdbcType=VARCHAR}


        and data_node_name = #{nodeName, jdbcType=VARCHAR}


        and source_type = #{sourceType, jdbcType=VARCHAR}

select id
from stream_source

    is_deleted = 0
    and status = 105

        and source_type in

            #{item}



        and agent_ip = #{agentIp, jdbcType=VARCHAR}

    and inlong_cluster_name = #{clusterName, jdbcType=VARCHAR}

mapper.update:
update stream_source
set previous_status = status,
status = #{nextStatus, jdbcType=INTEGER}

    , modify_time = modify_time

where id = #{id, jdbcType=INTEGER}

update stream_source
set previous_status = status,
status = #{nextStatus, jdbcType=INTEGER}

    is_deleted = 0
    and status != 101
    and inlong_group_id = #{groupId, jdbcType=VARCHAR}

        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

    and source_type != 'AUTO_PUSH'

update stream_source
set agent_ip = #{agentIp,jdbcType=VARCHAR},
uuid = #{uuid,jdbcType=VARCHAR}

    , modify_time = modify_time

where id = #{id, jdbcType=INTEGER}

update stream_source
is_deleted = id, previous_status = status, status = #{status, jdbcType=INTEGER}, version = version + 1
where is_deleted = 0 and inlong_group_id = #{groupId, jdbcType=VARCHAR} and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

mapper.delete:
delete
from stream_source
where
is_deleted = 0
and inlong_group_id = #{groupId, jdbcType=VARCHAR}

    and inlong_stream_id = #{streamId, jdbcType=VARCHAR}
